export declare const workerFile = "\nconst { parentPort } = require('worker_threads')\n\nparentPort.on('message', async message => {\n  const response = {\n    error: null,\n    data: null\n  }\n\n  try {\n    const [worker, isStatic, transferList] = message;\n    eval(worker)\n    // __executor__ is defined in worker\n    if(isStatic){\n      response.data = await __executor__(transferList)\n      if(response.data instanceof ArrayBuffer){\n        parentPort.postMessage(response, [response.data])\n      } else {\n        parentPort.postMessage(response)\n      }\n    } else {\n      response.data = await __executor__()\n      parentPort.postMessage(response)\n    }\n  } catch (err) {\n    response.data = null\n    response.error = {\n      message: err.message,\n      stack: err.stack\n    }\n\n    try {\n      parentPort.postMessage(response)\n    } catch (err) {\n      console.error(err)\n    }\n  }\n})\n";
